function counterPrice(id, sw, max){
    const lessBtn = document.querySelector(`.card__amount svg[data-id="less${id}"]`);
    const moreBtn = document.querySelector(`.card__amount svg[data-id="plus${id}"]`);
    const span = document.querySelector(`.card__amount span.data${id}`);
    let spanValue = span.textContent;

    let arrayID = id.split('');
    const input = document.getElementById(`pricePage${arrayID[1]}`);

    if (sw) {
        if (spanValue < max) {
            spanValue++;
            span.textContent = spanValue;
        }

        if (spanValue >= max) {
            moreBtn.classList.add('disabled');
        } else {
            moreBtn.classList.remove('disabled');
        }

        if (spanValue > 0) {
            lessBtn.classList.remove('disabled');
        }
    } else {
        if (spanValue > 0) {
            spanValue--;
            span.textContent = spanValue;
        }

        if (spanValue <= 0) {
            lessBtn.classList.add('disabled');
        } else {
            lessBtn.classList.remove('disabled');
        }

        if (spanValue < max) {
            moreBtn.classList.remove('disabled');
        }
    }

    calcTotal();
}

function updateBaggagePrice(formId) {
    let totalPrice = 0;

    const smallBagCheckbox = document.querySelector(`.small-bag-checkbox[data-form-id="${formId}"]`);
    const extraBagCheckbox = document.querySelector(`.extra-bag-checkbox[data-form-id="${formId}"]`);

    // Si se selecciona el segundo checkbox, agregar su valor
    if (extraBagCheckbox.checked) {
        totalPrice += 49900;
    }

    // Almacenar el precio en el array
    baggagePrices[formId] = totalPrice;

    // Actualizar el input con la suma total de todos los precios
    const totalSum = baggagePrices.reduce((acc, price) => acc + price, 0);
    priceBaggageInput.value = totalSum;
    calcTotal();
}

function calcTotal(){
    let totalBaggagePrice = 0;

    for (var i = 0; i < amountPassengers; i++) {
        const spanA = parseInt(document.querySelector(`.card__amount span.dataA${i}`).textContent) || 0;
        const spanB = parseInt(document.querySelector(`.card__amount span.dataB${i}`).textContent) || 0;
        const spanC = parseInt(document.querySelector(`.card__amount span.dataC${i}`).textContent) || 0;

        const spanPriceA = parseInt(document.querySelector(`.card__amount span.dataA${i}`).getAttribute('data-price')) || 0;
        const spanPriceB = parseInt(document.querySelector(`.card__amount span.dataB${i}`).getAttribute('data-price')) || 0;
        const spanPriceC = parseInt(document.querySelector(`.card__amount span.dataC${i}`).getAttribute('data-price')) || 0;

        const totalPage = (spanA * spanPriceA) + (spanB * spanPriceB) + (spanC * spanPriceC);

        document.getElementById(`pricePage${i}`).value = totalPage; // Asigna el total al input correspondiente

        totalBaggagePrice += totalPage; // Suma el total al totalBaggagePrice
    }

    // Asigna la suma total al input 'priceBaggage2'
    document.getElementById('priceBaggage2').value = totalBaggagePrice;

    let currentPrice = parseInt(document.getElementById('currentPrice').value);
    let checkBoxes = parseInt(document.getElementById('priceBaggage').value);
    totalBaggagePrice += checkBoxes + currentPrice;
    
    document.getElementById('newPrice').value = totalBaggagePrice;
    totalBaggagePrice = totalBaggagePrice.toLocaleString();
    document.getElementById('totalToPay').textContent = totalBaggagePrice;
}

function updateCarousel() {
    // Actualizar el contenido del carrusel con el pasajero actual
    carouselContent.innerHTML = `
        <div class="sub-header__element">
            <p class="sub-header__txt--strong">${elements[currentIndex].textStrong}</p>
            <p class="sub-header__txt--sub">${elements[currentIndex].textSub}</p>
        </div>
    `;

    // Mostrar solo el contenido de equipaje correspondiente al pasajero actual
    document.querySelectorAll('.card__baggage').forEach((el, index) => {
        el.style.display = index === currentIndex ? 'block' : 'none';
    });

    // Manejar la visibilidad y habilitación de las flechas
    prevArrow.style.visibility = currentIndex === 0 ? 'hidden' : 'visible';
    prevArrow.style.pointerEvents = currentIndex === 0 ? 'none' : 'auto';
    nextArrow.style.visibility = currentIndex === elements.length - 1 ? 'hidden' : 'visible';
    nextArrow.style.pointerEvents = currentIndex === elements.length - 1 ? 'none' : 'auto';
}

// Llamar a la función para actualizar el carrusel
updateCarousel();

// Manejo de eventos para las flechas
prevArrow.addEventListener('click', function() {
    if (currentIndex > 0) {
        currentIndex--;
        updateCarousel();
    }
});

nextArrow.addEventListener('click', function() {
    if (currentIndex < elements.length - 1) {
        currentIndex++;
        updateCarousel();
    }
});

// Llamar a las funciones después de generar el contenido
generateBaggageContent();
calcTotal();